#!/usr/bin/env python# -*- coding: utf-8 -*-"""-------------------------------------------------   File Name：     Description : 请求方法的封装层   Author :    sunyanfen   CreateDate：-------------------------------------------------"""import jsonimport requestsclass BaseApi:  params = {}  def send(self, data):      raw_data = json.dumps(data)      for key, value in self.params.items():          # todo：#如果输入的是list，如何取值          raw_data = raw_data.replace("${" + key + "}", value)      data = json.loads(raw_data)      return requests.request(**data).json()