#!/usr/bin/env python# -*- coding: utf-8 -*-"""-------------------------------------------------   File Name：     Description : 通讯录模块业务逻辑层   Author :    sunyanfen   CreateDate：-------------------------------------------------"""import yamlfrom api.baseapi import BaseApifrom api.util import Utilclass Maillist(BaseApi):    def __init__(self):        self.token = Util().get_token()        self.params["token"] = self.token        print(111)        with open("../api/user.yaml", encoding="utf-8") as f:            self.data = yaml.load(f)    def add(self, userid, name, mobile, department=None):        """创建用户"""        if department == None:            department = "1"        self.params["userid"] = userid        self.params["mobile"] = mobile        self.params["name"] = name        self.params["department"] = department        return self.send(self.data['add'])    def getinfo(self, userid):        """获取用户信息"""        self.params['userid'] = userid        return self.send(self.data['get'])    def update(self, userid, name, mobile):        """更新用户信息"""        self.params["userid"] = userid        self.params["mobile"] = mobile        self.params["name"] = name        return self.send(self.data['update'])    def delete(self,userid):        """删除接口"""        self.params['userid'] = userid        return self.send(self.data['delete'])